          Tcp tcp = packet.get(Tcp.class);
          if (ip!= null && tcp!= null) {
            dumper.dump(packet.getCaptureHeader(), packet.getRawData());
          }
        } catch (SSLHandshakeException e) {
          // Ignore SSL handshake exceptions
        }
      }
    }, null);
    dumper.close();
    pcap.close();
  }
}
